mapper.sql:
  {@each data.fields.rows as di, index}
    {@if di.isQueryConditionShow == "Y"}
         {@if di.DBTYPE == 'datetime' || di.DBTYPE == 'date'}

       	 and ${di.FIELDNAME} >= to_date(#{${di.FIELDNAME|getDcm}Begin}, 'yyyy-mm-dd hh24:mi:ss')


         and ${di.FIELDNAME} <= to_date(#{${di.FIELDNAME|getDcm}End}, 'yyyy-mm-dd hh24:mi:ss') 

         {@else}

		  ${di|generateMapperCondition}

         {@/if}
      {@/if}
  {@/each}

mapper.select:
	select t.* from (
 SELECT  , ROW_NUMBER () OVER (ORDER BY OID desc) AS RowNumber 
 from ${data.dataSourceNameTable}   



 ) t
 WHERE RowNumber BETWEEN {rows}*{page}-{rows}+1 AND {rows}*{page}

	SELECT COUNT(1) FROM (
 SELECT   from ${data.dataSourceNameTable}    



 ) t

	select t.* from (
 SELECT   
 from ${data.dataSourceNameTable}  



 ) t
